import { Text, Page, Image } from '@react-pdf/renderer'

export const Body = Page`
  padding-top: 35px;
  padding-bottom: 65px;
  padding-right: 35px;
  padding-left: 35px;
`;

const Header = styled.Text`
  color: grey;
  font-size: 12px;
  text-align: center;
  margin-bottom: 20px;
`;

const Title = styled.Text`
  font-size: 24px;
  text-align: center;
  font-family: 'Oswald';
`;

const Subtitle = styled.Text`
  margin: 12px;
  font-size: 18px;
  font-family: 'Oswald';
`;

const Author = styled.Text`
  font-size: 12px;
  text-align: center;
  margin-bottom: 40px;
`;

const Paragraph = styled.Text`
  margin: 12px;
  font-size: 14px;
  text-align: justify;
  font-family: 'Times-Roman';
`;

const Picture = styled.Image`
  margin: 15px 100px;
`;

const Footer = styled.Text`
  left: 0px;
  right: 0px;
  color: grey;
  bottom: 30px;
  font-size: 12px;
  position: absolute;
  text-align: center;
`;
