import React, { Component } from 'react';
import {Container, Jumbotron} from 'react-bootstrap';

import OrganizationForm from './OrganizationForm';
import DataProcessingForm from './DataProcessingForm';
import DataUsersForm from './DataUsersForm';
import DataDonorsForm from './DataDonorsForm';
import Success from './Success';
import InfoTable from './InfoTable';
import PersonalInfo from './CanadaFlow';
import UserInfo from './UserInfo';

class MainForm extends Component {

    constructor(props) {
        super(props);

        this.state = {
            step: 1,
            isUserKnown: false,
            userType: '',
            currentLocation: '',
            currentForm: 'organization',
            data: [
              { type: 'organization',
                print: 'Where is the organization?',
                location: '',
               },
              { type: 'dataProcessed',
                print: 'Where is the data processed?',
                location: '',
              },
              { type: 'dataUsers',
                print: 'Where are the data users?',
                location: '',
              },
              {
                type: 'dataDonors',
                print: 'Where are the data donors?',
                location: '',
              },
            ]
        }
    }

    nextStep = () => {
        const { step } = this.state;
        this.setState({
            step : step + 1
        })
    }

    prevStep = () => {
      const { step, isLocationKnown } = this.state;
      if(isLocationKnown){
        this.setState({
          isLocationKnown: false
        });
      } else {
        this.setState({
            step : step - 1
        });
      }
    }

    handleLocChange = (input) => {
      var { data } = this.state;
      console.log(data);
      for (var i in data) {
         if (data[i].type == input.type) {
           data[i] = {
                type:input.type,
                location: input.location,
                isPerfonalInfo:input.isPerfonalInfo,
            }
            this.setState({
              data : data,
              isLocationKnown: true,
              currentLocation: input.location,
              currentForm: input.type
            });
         }
      }
    }

    handleInfoChange  = (input) => {
      var { data } = this.state;
      console.log(data);
      for (var i in data) {
         if (data[i].type == input.type) {
           data[i] = {
                type:input.type,
                location: input.location,
                isPerfonalInfo:input.isPerfonalInfo,
            }
            this.setState({
              data : data,
              isLocationKnown: false,
              currentLocation: input.location
            });
         }
      }
    }

    handleUserChange  = (input) => {
      var { data } = this.state;
      this.setState({
        userType: input,
        isUserKnown: true
      });
    }

    getLocationComponent = () => {
      const { step } = this.state;
      var Form, currentForm;
      switch(step) {
      case 1:
          Form = OrganizationForm;
          currentForm = 'organization';
          break;
      case 2:
          Form = DataProcessingForm;
          currentForm = 'dataProcessed';
          break;
      case 3:
          Form = DataUsersForm;
          currentForm = 'dataUsers';
          break;
      case 4:
          Form = DataDonorsForm;
          currentForm = 'dataDonors';
          break;
      case 5:
          Form = Success;
          currentForm = 'sucess';
          break;
      }
      return Form;
    }

    render(){
        const {step, isUserKnown, data, currentLocation, currentForm} = this.state;
        const Component = isUserKnown ? this.getLocationComponent() : UserInfo;
        return(
        <Container>
          <div>
            <Jumbotron>
              <Component
                nextStep={this.nextStep}
                prevStep={this.prevStep}
                handleLocChange={this.handleLocChange}
                handleInfoChange={this.handleInfoChange}
                handleUserChange={this.handleUserChange}
                location= {currentLocation}
                currentForm={currentForm}
              />
            </Jumbotron>
            <InfoTable values={data}/>
          </div>
        </Container>)
    }
}

export default MainForm;
