import React from "react";
import Particles from "react-particles-js";

export default () => (
  <div
    style={{
      position: "absolute",
      top: 0,
      left: 0,
      width: "100%",
      height: "100%",
      backgroundColor: 'rgb(10, 91, 117)'
    }}
  >
  <Particles
     height={window.outerHeight}
      params={{
        "particles": {
            "number": {
                "value": 200
            },
            "size": {
                "value": 5
            }
        },
        "interactivity": {
            "events": {
                "onhover": {
                    "enable": true,
                    "mode": "repulse"
                },
                "onresize": {
                   "enable": true,
                   "density_auto": true,
                   "density_area": 400 // nb_particles = particles.nb * (canvas width *  canvas height / 1000) / density_area
                 }
            }
        }
    }} />
  </div>
);
