import React, { Component } from 'react';
import { Form, ButtonGroup, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';

class OrganizationForm extends Component{

  constructor(props) {
   super(props);

   this.state = {
     location: '',
     isPerfonalInfo: false
   }

   // This binding is necessary to make `this` work in the callback
   this.saveAndContinue = this.saveAndContinue.bind(this);
  }

    saveAndContinue = (e, value) => {
        e.preventDefault();
        this.props.nextStep();
        this.props.handleChange(value);
    }

    createType = (location) => {

      return ({
        type : 'organization',
        location: location,
        isPerfonalInfo: this.state.isPersonalInfo

      })
    }

    render(){
        const { values } = this.props;
        return(
          <div>
            <h1 style={{paddingBottom: '2%'}}> Where is the project/organization established?</h1>
            <ButtonGroup style={{width:'100%'}} size="lg" vertical>
              <Button style={{opacity:'10%'}} variant="secondary" onClick={(e) => this.saveAndContinue(e, this.createType('Canada') )}>Canada</Button>
              <Button onClick={(e) => this.saveAndContinue(e, this.createType('Europe'))}>Europe</Button>
              <Button onClick={(e) => this.saveAndContinue(e, this.createType('United States'))}>United States</Button>
            </ButtonGroup>
            <div style={{paddingTop: '3%'}}>
              <Link to="/">
                <Button variant="danger"> Restart  </Button>
              </Link>
            </div>
          </div>
        )
    }
}

export default OrganizationForm;
