import styled from 'styled-components/react';

export const Body = styled.Page`
  padding-top: 35px;
  padding-bottom: 65px;
  padding-right: 35px;
  padding-left: 35px;
`;

export const Header = styled.Text`
  color: grey;
  font-size: 12px;
  text-align: center;
  margin-bottom: 20px;
`;

export const Title = styled.Text`
  font-size: 24px;
  text-align: center;
  font-family: 'Oswald';
`;

export const Subtitle = styled.Text`
  margin: 12px;
  font-size: 18px;
  font-family: 'Oswald';
`;

export const Author = styled.Text`
  font-size: 12px;
  text-align: center;
  margin-bottom: 40px;
`;

export const Paragraph = styled.Text`
  margin: 12px;
  font-size: 14px;
  text-align: justify;
  font-family: 'Times-Roman';
`;

export const Picture = styled.Image`
  margin: 15px 100px;
`;

export const Footer = styled.Text`
  left: 0px;
  right: 0px;
  color: grey;
  bottom: 30px;
  font-size: 12px;
  position: absolute;
  text-align: center;
`;
