import React, { Component } from 'react';
import OrganizationForm from './OrganizationForm';
import DataProcessingForm from './DataProcessingForm';
import DataUsersForm from './DataUsersForm';
import DataDonorsForm from './DataDonorsForm';
import Success from './Success';
import InfoTable from './InfoTable'

class MainForm extends Component {

    constructor(props) {
        super(props);

        this.state = {
            step: 1,
            data: [
              { type: 'organization',
                location: '',
                isPerfonalInfo: null
               },
              { type: 'dataProcessed',
                location: '',
                isPerfonalInfo: null
              },
              { type: 'dataUsers',
                location: '',
                isPerfonalInfo: null
              },
              {
                type: 'dataDonors',
                location: '',
                isPerfonalInfo: null
              },
            ]
        }

    }

    nextStep = () => {
        const { step } = this.state
        this.setState({
            step : step + 1
        })
    }

    prevStep = () => {
        const { step } = this.state
        this.setState({
            step : step - 1
        })
    }

    handleChange = input => event => {
      const data = this.state.data
      for (var i in data) {
         if (data[i].type == input.type) {
           data[i] = {
                type:input.type,
                location: input.location,
                isPerfonalInfo:input.isPerfonalInfo,
            }
            this.setState({ data : data});
         }
      }
    }

    render(){
        const {step, data} = this.state;
        var FormComponent = null;
        switch(step) {
        case 1:
            FormComponent = OrganizationForm;
        case 2:
            FormComponent = DataProcessingForm;
        case 3:
            FormComponent = DataUsersForm;
        case 4:
            FormComponent = DataDonorsForm;
        case 5:
            FormComponent = Success;
        return <>
                <FormComponent
                  nextStep={this.nextStep}
                  prevStep={this.prevStep}
                  handleChange = {this.handleChange}
                />
                <InfoTable
                  values={data}
                />
               </>
        }
    }
}

export default MainForm;
